const { createUser, getUsers, getOneUser } = require("../../data/user.js")

// Função para criar User e fazer algo... (se vazio: só cria)
async function newUser(data) {
    return await createUser(data)
}

// Função para chamar todos os Users e fazer algo... (se vazio: só obtem)
async function findUsers() {
    return await getUsers()
}

// Função para chamar um User e fazer algo... (se vazio: só obtem)
async function findOneUser (data) {
    return await getOneUser(data)
}

module.exports = { newUser, findUsers, findOneUser };